package JAVAbasic;

public class Flow_Control5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10,b =80, c =7 ,d=2;
		
		if (a>b) {
		if (a >c) {
		if (a>d) {
			
			System.out.println("A is Big Number");
		}
		
		}

		}
		else
			
			System.out.println("A is not a Big Number");
		
	}

}
