package JAVAbasic;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Print 1 to 10
		
		int i = 1;
		
		while (i<=10) {
			
			System.out.println(i);
			i++;
		}
		
	// Print 10 to 1
		
		int b = 10;
		
		while (b>=1) {
			
			System.out.println(b);
			b--;
		
		
	}

		
		// Print 1 to 10 without 7
		
				int a = 1;
				
				while (a<=10) {
					
					if (a != 7) {
					
					System.out.println(a);
					}
					a++;
}}}


